mapper.update:
update tbl_feedback
set feedback=#{feedback}
where id = #{id}

mapper.select:
select f.id, f.title_feedback, f.time, s.name from tbl_feedback f join tbl_student s on s.id=f.stu_id where f.status='2' and f.stu_id in
#{item.id}
limit #{pageNo},#{pageSize}

mapper.update:
update tbl_feedback
set status='3'
where id = #{id}

mapper.select:
select f.id as 'id', f.time as 'time', r.time as 'replyTime', s.name as 'name', f.title_feedback as 'titleFeedback' from tbl_feedback f join tbl_student s on f.stu_id=s.id join tbl_reply r on f.id=r.feedback_id where f.status='3' and f.del_flag='0' and f.stu_id in
#{item.id}
limit #{pageNo},#{pageSize}
