mapper.select:
select count(*)
from tbl_library
where building_id = #{buildingId}

select id, floor, area, seat, name, status, library_id from tbl_lib_table where library_id = #{vo.libraryId}
and floor like '%' #{vo.floor} '%'
and area like '%' #{vo.area} '%'
and status like '%' #{vo.status} '%'
order by floor, name limit #{vo.pageNo}, #{vo.pageSize}

select id
from tbl_library
where name = #{name}

select sum(seat) - (select ifnull(sum(seat), 0) from tbl_lib_table where status = 1 and floor = #{floor})
from tbl_lib_table
where library_id = #{library}
    and floor = #{floor}
