mapper.select:
select salt
from tbl_tutor
where id = #{id}

select count(*)
from tbl_tutor
where username = #{username}

mapper.update:
update tbl_tutor
set  remark=#{remark}
where id=#{id}

update tbl_tutor
set salt=#{salt},
    password=#{password}
    where id=#{id}

update tbl_tutor
set avatar_path = #{avatarPath}
where username = #{username}
